import React from 'react';
import './Person.css';

const person = ( props ) => {
    return (
        <div className = "Person">
            <p>I'm {props.name} and I am {props.age} years old!</p>
            <p>{props.children}</p>
            <input type="text" onChange={this.nameChangeHandler} />
        </div>
    )
};

export default person;
