import React, { Component } from 'react';
import './css/main.css';
import classes from './App.module.css';
//import Radium, { StyleRoot } from 'radium';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
    { id: 'asd',name: 'Max', age: 28 },
    { id: 'as23',name: 'Manu', age: 29 },
    { id: 'a343dfa',name: 'Stephanie', age: 26 }
    ],
    otherState: 'some other value',
    showPersons: false
  };

  deletePersonHandler = (personIndex) => {
    //const persons = this.state.persons.slice(); //copies array
    const persons = [...this.state.persons]; //another way to copy but more modern with the spread operator ...
    persons.splice(personIndex, 1);
    this.setState({persons: persons});
  };

  nameChangeHandler = (event, id) => {
    // console.log('Was clicked!');
    // DON'T DO THIS: this.state.persons[0].name = 'Maximilian';
    const personIndex = this.state.persons.findIndex(p => {
      return p.id === id;
    });

    const person = {
      ...this.state.persons[personIndex]
    };

    person.name = event.target.value;

    const persons = [...this.state.persons];
    persons[personIndex] = person;

    this.setState({ persons: persons
    });
  };

  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  };

  render() {

    const style = {
      backgroundColor: 'green',
      color: 'white',
      font: 'inherit',
      border: '1x solid blue',
      padding: '8px',
      cursor: 'pointer',
      /*
      ':hover': {
        backgroundColor:'lightgreen',
        color: 'black'
      }
      */
    };

    let persons = null;

    if (this.state.showPersons) {
      persons = (
        <div>
        {this.state.persons.map((person, index) => {
          return <Person 
          click={() => this.deletePersonHandler(index)}
          name={person.name}
          age={person.age}
          key={person.id}
          changed={(event) => this.nameChangeHandler(event, person.id)} />
        })} 
        </div>
        );
      style.backgroundColor = 'red';
      /*
      style[':hover'] = {
        backgroundColor:'salmon',
        color: 'black'
      }
      */
    }

    //let classes = ['red', 'bold'].join(' ');
    let assignedClasses = [];
    if (this.state.persons.length <=2) {
      assignedClasses.push(classes.red); //classes = ['red']
    }
    if (this.state.persons.length <=1) {
      assignedClasses.push(classes.bold); //classes = ['red', 'bold']
    }

    return (
      //<StyleRoot>
      <div className={classes.App}>
      <h1>Hi, I'm a React App</h1>
      <p className={assignedClasses.join(' ')}>This is really working!</p>
      <button
      style={style} 
      onClick={this.togglePersonsHandler}>Toggle Persons</button>
      {persons}

        <header>
        <a
          className="App-link"
          href="http://mamaspizzas.net/"
          target="_blank"
          rel="noopener noreferrer"
        >
          Eat Pizza
        </a>
      </header>
      <body>
        <div className="callout">
          <p className={assignedClasses.join(' ')}>
            My name is Miles Cruz, web developer and bass player!
            Thank you for taking the time to visit my site!
          </p>
        </div>
        <div className="section">
          <p><span>
            Oh yes!
          </span></p>
        </div>
      </body>
      <footer></footer>

      </div>
      //</StyleRoot>
      );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
  }
}

export default App;
